if(typeof window=="undefined")var assert=require("assert");suite("monster",function(){var e=function(e){var t=document.cookie.split(";");for(var n=0,r=t.length;n<r;n++){var i=t[n].split("="),s=i[0].replace(/ /g,""),o=i[1];if(e==s)return o}return null};suite("#set",function(){test("should set string",function(){var t="name",n="value",r=1;monster.set(t,n,r);assert.equal(e(t),n)});test("should set number",function(){var t="name",n=1,r=1;monster.set(t,n,r);assert.equal(e(t),n.toString())});test("should set object",function(){var t="name",n={test:1};monster.set(t,n);assert.equal(e(t),'{"v":{"test":1}}')});test("should set array",function(){var t="name",n=["some","value"],r=1;monster.set(t,n,r);assert.equal(e(t),'{"v":["some","value"]}')});test("should set undefined",function(){var t="name",n=undefined,r=1;monster.set(t,n,r);assert.equal(e(t),"undefined")});test("should set null",function(){var t="name",n=null,r=1;monster.set(t,n,r);assert.equal(e(t),'{"v":null}')});test('should set string edge case string starting with "["',function(){var t="name",n="[something edgy",r=1;monster.set(t,n,r);assert.equal(e(t),"%5Bsomething%20edgy")});test('should set string edge case string starting with "{"',function(){var t="name",n="{something edgy",r=1;monster.set(t,n,r);assert.equal(e(t),"%7Bsomething%20edgy")});test("should try to set an object in a browser that dont have window.JSON",function(){var e="name",t={some:"value"},n=1,r=window.JSON;window.JSON=null;window.JSON=r})});suite("#get",function(){test("should get string",function(){var e="name",t="value",n=1;monster.set(e,t,n);assert.equal(monster.get(e),t)});test("should get number",function(){var e="name",t=1,n=1;monster.set(e,t,n);assert.equal(monster.get(e),t.toString())});test("should get object",function(){var e="name",t={test:1};monster.set(e,t);assert.deepEqual(monster.get(e),t)});test("should get array",function(){var e="name",t=["some","value"],n=1;monster.set(e,t,n);assert.deepEqual(monster.get(e),t)});test("should get undefined",function(){var e="name",t=undefined,n=1;monster.set(e,t,n);assert.equal(monster.get(e),t)});test("should get null",function(){var e="name",t=null,n=1;monster.set(e,t,n);assert.equal(monster.get(e),t)});test('should get string edge case string starting with "["',function(){var e="name",t="[something edgy",n=1;monster.set(e,t,n);assert.equal(monster.get(e),t)});test('should get string edge case string starting with "{"',function(){var e="name",t="{something edgy",n=1;monster.set(e,t,n);assert.equal(monster.get(e),t)});test("should try to set an object in a browser that dont have window.JSON",function(){var e="name",t={some:"value"},n=1,r=window.JSON;window.JSON=null;assert.throws(function(){monster.set(e,t,n)});window.JSON=r})});suite("#remove",function(){test("should remove cookie",function(){var t="name",n="value",r=1;monster.set(t,n,r);monster.remove(t);assert.equal(e(t),null)})})});